from langflow.custom import Component
from langflow.io import DataInput, MultilineInput, Output
from langflow.schema.message import Message
import pandas as pd
import logging

# Set up logging
logging.basicConfig(level=logging.INFO)
logger = logging.getLogger(__name__)

class SocialMetricsComponent(Component):
    display_name = "Social Media Metrics"
    description = "Analyzes social media metrics data"
    icon = "chart-bar"
    name = "SocialMetrics"

    inputs = [
        DataInput(
            name="data",
            display_name="Social Media Data",
            info="Input social media metrics data",
        ),
        MultilineInput(
            name="template",
            display_name="Template",
            info="Output template format",
            value="""
Post Analysis:
ID: {post_id}
Type: {post_type}
Metrics:
- Likes: {likes:,}
- Comments: {comments:,}
- Shares: {shares:,}
- Total Engagement: {total:,}
Performance: {engagement_level}
-------------------
            """.strip()
        )
    ]

    outputs = [
        Output(display_name="Analysis Results", name="analysis", method="analyze_metrics")
    ]

    def _get_engagement_level(self, total: int) -> str:
        """Determine engagement level based on total engagement"""
        if total > 1000:
            return "High Engagement 🌟"
        elif total > 500:
            return "Medium Engagement ⭐"
        return "Low Engagement 📊"

    def _safe_convert(self, value) -> int:
        """Safely convert values to integers with better error handling"""
        try:
            if pd.isna(value):
                return 0
            return int(float(str(value).strip()))
        except:
            logger.warning(f"Could not convert value '{value}' to integer, using 0")
            return 0

    def _process_input_data(self, input_data) -> pd.DataFrame:
        """Process input data into a DataFrame with improved error handling"""
        try:
            # Handle different input types
            if isinstance(input_data, list):
                df = pd.DataFrame(input_data)
            elif isinstance(input_data, dict):
                if any(isinstance(v, list) for v in input_data.values()):
                    # Handle nested list in dictionary
                    for value in input_data.values():
                        if isinstance(value, list):
                            df = pd.DataFrame(value)
                            break
                else:
                    df = pd.DataFrame([input_data])
            else:
                df = pd.DataFrame(input_data)

            logger.info(f"Created DataFrame with shape: {df.shape}")
            logger.info(f"Columns: {', '.join(df.columns)}")

            # Convert numeric columns
            numeric_columns = ['Likes', 'Shares', 'Comments', 'post_id']
            for col in numeric_columns:
                if col in df.columns:
                    df[col] = pd.to_numeric(df[col], errors='coerce')

            # Standardize column names
            column_mapping = {
                'likes': 'likes',
                'comments': 'comments',
                'shares': 'shares',
                'Likes': 'likes',
                'Comments': 'comments',
                'Shares': 'shares'
            }
            
            # Rename columns based on mapping
            df = df.rename(columns=column_mapping)

            return df

        except Exception as e:
            logger.error(f"Error processing input data: {e}")
            raise ValueError(f"Failed to process input data: {str(e)}")

    def analyze_metrics(self) -> Message:
        try:
            if not self.data:
                return Message(text="Error: No data provided")

            # Get input data
            input_data = self.data.data if hasattr(self.data, 'data') else self.data
            
            # Process data into DataFrame
            df = self._process_input_data(input_data)
            logger.info(f"Processing DataFrame with columns: {df.columns}")
            
            # Process posts
            results = []
            total_engagement = 0
            
            for idx, row in df.iterrows():
                # Log the current row for debugging
                logger.info(f"Processing row: {row}")

                # Create metrics dictionary with default values
                metrics = {
                    'post_id': str(row.get('post_id', f'Post_{idx+1}')),
                    'post_type': str(row.get('post_type', 'Standard Post')),
                    'likes': self._safe_convert(row.get('likes', 0)),
                    'comments': self._safe_convert(row.get('comments', 0)),
                    'shares': self._safe_convert(row.get('shares', 0))
                }
                
                # Calculate total and engagement level
                metrics['total'] = sum([metrics['likes'], metrics['comments'], metrics['shares']])
                total_engagement += metrics['total']
                metrics['engagement_level'] = self._get_engagement_level(metrics['total'])
                
                try:
                    formatted_result = self.template.format(**metrics)
                    results.append(formatted_result)
                except KeyError as e:
                    logger.error(f"Template formatting error: {e}")
                    return Message(text=f"Template error: Missing field {str(e)}")

            # Create summary with statistics
            avg_engagement = total_engagement / len(results) if results else 0
            output = f"""
Social Media Analysis Report
==========================
Total Posts Analyzed: {len(results)}
Total Engagement: {total_engagement:,}
Average Engagement Per Post: {avg_engagement:,.1f}

Detailed Analysis:
{"\n".join(results)}
            """.strip()

            return Message(text=output)

        except Exception as e:
            logger.error(f"Analysis error: {e}")
            return Message(text=f"Error during analysis: {str(e)}")

